DROP TABLE IF EXISTS `om_order`;
CREATE TABLE `om_order` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '订单id',
  `memberId` bigint(20) NOT NULL COMMENT '会员id',
  `orderSn` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '订单编号',
  `orderTime` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '下单时间',
  `memberUsername` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '用户帐号',
  `totalAmount` decimal(10,2) DEFAULT NULL COMMENT '订单总金额',
  `payAmount` decimal(10,2) DEFAULT NULL COMMENT '应付金额（实际支付金额）',
  `freightAmount` decimal(10,2) DEFAULT NULL COMMENT '运费金额',
  `promotionAmount` decimal(10,2) DEFAULT NULL COMMENT '促销优化金额（促销价、满减、阶梯价）',
  `payType` int(1) DEFAULT NULL COMMENT '支付方式：0->未支付；1->支付宝；2->微信',
  `sourceType` int(1) DEFAULT NULL COMMENT '订单来源：0->PC订单；1->app订单',
  `status` int(1) DEFAULT NULL COMMENT '订单状态：0->待付款；1->待发货；2->已发货；3->已完成；4->已关闭；5->无效订单',
  `orderType` int(1) DEFAULT NULL COMMENT '订单类型：0->正常订单；1->活动订单',
  `deliveryCompany` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '物流公司(配送方式)',
  `deliverySn` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '物流单号',
  `autoConfirmDay` int(11) DEFAULT NULL COMMENT '自动确认时间（天）',
  `integration` int(11) DEFAULT NULL COMMENT '可以获得的积分',
  `growth` int(11) DEFAULT NULL COMMENT '可以活动的成长值',
  `receiverName` varchar(100) COLLATE utf8_bin NOT NULL COMMENT '收货人姓名',
  `receiverPhone` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '收货人电话',
  `receiverPostCode` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '收货人邮编',
  `receiverProvince` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '省份/直辖市',
  `receiverCity` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '城市',
  `receiverRegion` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '区',
  `receiverDetailAddress` varchar(200) COLLATE utf8_bin DEFAULT NULL COMMENT '详细地址',
  `note` varchar(500) COLLATE utf8_bin DEFAULT NULL COMMENT '订单备注',
  `confirmStatus` int(1) DEFAULT NULL COMMENT '确认收货状态：0->未确认；1->已确认',
  `deleteStatus` int(1) NOT NULL DEFAULT '0' COMMENT '删除状态：0->未删除；1->已删除',
  `paymentTime` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '支付时间',
  `deliveryTime` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '发货时间',
  `receiveTime` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '确认收货时间',
  `commentTime` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '评价时间',
  `modifyTime` varchar(64) COLLATE utf8_bin DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

